import React, {useEffect} from 'react';
import {useNavigate, useParams} from "react-router-dom";
import {useTypedSelector} from "../../hooks/useTypedSelector";
import {fetchWeater} from "../../redux/action-creators/fetchWeater";
import {useFetchWeather} from "../../hooks/useFetchWeather";

const SevenDays = () => {
    type IParams={
        city:string
    }
    const params = useParams<IParams>();
    let city:string;
    const defaultCity = useTypedSelector(state=>state.weather.resolvedAdress);
    const navigate = useNavigate();
    useEffect(()=>{
        if(params.city){
            city=params.city;
        }
        else{
            navigate('/'+defaultCity);
        }
    },[])
    const daysArray = useTypedSelector(state=>state.weather?.days);
    return (
        <div>
            <h1>7 days</h1>
        </div>
    );
};

export default SevenDays;
