import {useDispatch} from "react-redux";
import {IActionsTypeModal,ActionTypesModal} from "../reducers/modalSearchReducer";
import {Dispatch} from "redux";

export const openModal = ()=>{
    return (dispatch:Dispatch<IActionsTypeModal>) =>dispatch({type:ActionTypesModal.modalOpen})
}
export const closeModal = ()=>{
    return (dispatch:Dispatch<IActionsTypeModal>) =>dispatch({type:ActionTypesModal.modalClose})
}
