import React, {FC} from 'react';
import './TodayForecast.scss';
import sun from '../../assets/sun.png';
import cloud from '../../assets/cloud.png';
import {useTypedSelector} from "../../hooks/useTypedSelector";
import {ICurrentSate} from "../../redux/reducers/weatherReducer";
import {useNormalDate} from "../../hooks/useNormalDate";
import {CSSTransition} from "react-transition-group";
import ModalSearch from "../ModalSearch/ModalSearch";
import {ActionTypesModal} from "../../redux/reducers/modalSearchReducer";
import {useDispatch} from "react-redux";

const TodayForecast: FC = () => {
    const isOpenModal = useTypedSelector(state => state.modal.isOpen);
    const currentWeatherState: ICurrentSate = useTypedSelector(state => state.weather?.currentState);
    const currentCity: string = useTypedSelector(state => state.weather?.resolvedAdress);
    const date: string = useNormalDate(currentWeatherState);
    const openModalMenu = ()=>{
        dispatch({type: ActionTypesModal.modalOpen})
    }
    const dispatch = useDispatch();
    return (
        <div className='app__todayWrapper'>
            <div className="app__city">{currentCity}</div>
            <div className='app__changeBtn'>
                <button onClick={openModalMenu}>Change <span
                    className='app__burger-menu'></span></button>
            </div>
            {isOpenModal ? <div className="background" onClick={()=>dispatch({type:ActionTypesModal.modalClose})}></div> : ''}
            <CSSTransition in={isOpenModal} unmountOnExit mountOnEnter
                           timeout={350} classNames='my-modal'>
                <ModalSearch/>
            </CSSTransition>
            <div className="app__imgWrapper">
                <img src={currentWeatherState?.conditions !== 'Clear' ? cloud : sun} alt=""/>
            </div>
            <div className='app__someInfo'>
                <div className='app__temp'>{currentWeatherState?.temp}</div>
                <div className='app__conditions'>{currentWeatherState?.conditions}</div>
            </div>
            <div className='app__date'>{date}</div>
            <div className='app__addInfo'>
                <div className="app__windSpeed addInfoItem">{currentWeatherState?.windspeed} mph</div>
                <div className="app__pressure addInfoItem">{currentWeatherState?.pressure} pa</div>
                <div className="app__cloudCover addInfoItem">{currentWeatherState?.cloudcover}%</div>
            </div>
        </div>
    );
};

export default TodayForecast;
