import {useActions} from "./useActions";
import {useEffect} from "react";
import {useDispatch} from "react-redux";
import {ActionTypes} from "../redux/types/action-types";
import {WeatherApi} from "../API/WeatherApi";

export const useFetchWeather = (city:string) => {
    const {fetchWeater} = useActions();
    const dispatch = useDispatch();
    const fetchWeatherFunc = async () => {
        try {
            dispatch({type: ActionTypes.FETCH_WEATHER});
            const weather = await WeatherApi.getWeatherByCity(city);
            return fetchWeater(weather);
        } catch (e) {
            dispatch({type: ActionTypes.FETCH_WEATHER_ERROR, payload: 'error 404'});
        }
    }
    useEffect(() => {
        fetchWeatherFunc();
    }, []);
}
