import React, {FC, useState} from 'react';
import Loader from "../Loader/Loader";
import {TextField} from "@mui/material";
import {WeatherApi} from "../../API/WeatherApi";
import {useActions} from "../../hooks/useActions";
import {closeModal} from "../../redux/action-creators/Modal";
interface IModalProps{
    searchValue:string
}
const ModalSearch:FC  = () => {
    const {fetchWeater} = useActions();
    const [searchValue, setSearchValue] = useState<string>('');
    const [isLoading, setIsLoading] = useState<boolean>(false);const searchWeatherHandler = async () => {
        try {
            setIsLoading(true);
            const weather = await WeatherApi.getWeatherByCity(searchValue);
            closeModal();
            setIsLoading(false);
            return fetchWeater(weather);
        } catch (e) {
            setIsLoading(false);
            return console.log(e);
        }
    }
    return (
        <div className='app__my-modal'>
            {isLoading ? <Loader/> :
                <>
                    <TextField className='muiInput' value={searchValue}
                               id="standard-basic" label="City"
                               variant="standard"
                               onChange={(e) => setSearchValue(e.target.value)}
                               type="text"/>
                    <button className='app__modalConfirmButton' onClick={searchWeatherHandler}>Confirm</button>
                </>}
        </div>
    );
};

export default ModalSearch;
