import {ActionTypes, IActions, IWeather} from "../reducers/weatherReducer";
import {Dispatch} from "redux";

export const fetchWeater =(payload:IWeather)=>{
    return async(dispatch:Dispatch<IActions>)=>{
        try{
            dispatch({type:ActionTypes.FETCH_WEATHER});
            dispatch({type: ActionTypes.FETCH_WEATHER_SUCCESS,payload});
        }
        catch(e){
            dispatch({type:ActionTypes.FETCH_WEATHER_ERROR});
        }
    }
}
