export enum ActionTypes {
    FETCH_WEATHER = 'FETCH_WEATHER',
    FETCH_WEATHER_SUCCESS = 'FETCH_WEATHER_SUCCESS',
    FETCH_WEATHER_ERROR = 'FETCH_WEATHER_ERROR',
}

export interface IActions {
    type: ActionTypes
    payload?: any
}

interface IWeatherDays {
    datetime: string,
    tempmax: number,
    tempmin: number,
    conditions: string
}

interface ICurrentSate {
    temp: string,
    windspeed: number,
    cloudcover: number,
    conditions: string,
    feelslike: number
}

export interface IWeather {
    resolvedAdress: string,
    days?: Array<IWeatherDays>
    currentState?: ICurrentSate,
    isLoading:boolean,
    error?:string|null
}

const defaultState: IWeather = {
    resolvedAdress: 'Kyiv',
    isLoading:false,
    error:null
}
export const weatherReducer = (state = defaultState, action: IActions) => {
    switch (action.type) {
        case ActionTypes.FETCH_WEATHER_SUCCESS:
            return {
                ...state,error:null, resolvedAdress: action.payload.resolvedAdress,
                days: action.payload.days, currentState: action.payload.currentConditions
            }
            case ActionTypes.FETCH_WEATHER: return{...state,error:null, isLoading:true}
            case ActionTypes.FETCH_WEATHER_ERROR: return{...state,error:action.payload,isLoading:true}
        default:
            return state;
    }
}
