import React from 'react';
import './App.scss';
import {useFetchWeather} from "./hooks/useFetchWeather";
import {useTypedSelector} from "./hooks/useTypedSelector";
import {useTheme} from "./hooks/useTheme";
import TodayForecast from "./components/TodayForecast/TodayForecast";
import Loader from "./components/Loader/Loader";
import {BrowserRouter, Route, Routes} from "react-router-dom";

const App: React.FC = () => {
    const city: string = useTypedSelector(state => state.weather.resolvedAdress);
    const isLoading: boolean = useFetchWeather(city);
    useTheme();
    const currentTheme: string = useTypedSelector(state => state.currentPage.bgColor);
    if (isLoading) {
        return <div className='app__LoaderWrapper'><Loader marginTop={200}/></div>
    }
    return (
        <BrowserRouter>
            <Routes>
                <div className={currentTheme === 'gray' ? 'app gray' : 'app blue'}>
                    <div className='app__wrapper'>
                        <Route
                            path="*"
                            element={<Navigate to="/" replace />}
                        />
                        <TodayForecast/>
                    </div>
                </div>
            </Routes>
        </BrowserRouter>
    );
}

export default App;
