import {useActions} from "./useActions";
import {useEffect} from "react";
import axios from "axios";
import {IWeather} from "../redux/reducers/weatherReducer";

interface IusefetchWeather{
    setIsLoading:()=>void;
}

export const useFetchWeather = (props:IusefetchWeather) => {
    const {fetchWeater} = useActions();
    const fetchWeaterFunc = async()=>{
        try{
            const response = await axios.get<IWeather>('https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/kyiv?unitGroup=metric&key=KXLCQFR4Q8PKE8LW86YXF7QWD&contentType=json');
            const weather = response.data;
            return fetchWeater(weather);
        }
        catch (e){
            console.log(e);
        }
    }
    useEffect(() => {
        props.setIsLoading(true);
        fetchWeaterFunc();
    }, []);
}
