import React, {useEffect, useMemo} from 'react';
import {useNavigate, useParams} from "react-router-dom";
import {useTypedSelector} from "../../hooks/useTypedSelector";
import {IWeatherDays} from "../../redux/reducers/weatherReducer";
import axios from "axios";

const SevenDays = () => {
    type IParams={
        city:string
    }
    const params = useParams<IParams>();
    const defaultCity = useTypedSelector(state=>state.weather.resolvedAdress);
    const daysArray:IWeatherDays[] = useTypedSelector(state=>state.weather?.days);
    let city:string = useMemo<string>(()=>{
        if(params.city)
            return params.city
        return defaultCity;
    },[]);
    const navigate = useNavigate();
    useEffect(()=>{
        if(daysArray.length===0){
            axios.get('')
        }
    },[]);
    return (
        <div>
            <h1>7 days</h1>
        </div>
    );
};

export default SevenDays;
