import React, {useState} from 'react';
import Loader from "../../components/Loader/Loader";
import {useFetchSevenDays} from "../../hooks/useFetchSevenDays";
import IconsRow from "../../components/IconsRow/IconsRow";
import {useNormalDate} from "../../hooks/useNormalDate";
import sun from '../../assets/sun.png';
import cloud from '../../assets/cloud.png';
import './SevenDays.scss';
import {useDispatch} from "react-redux";
import {ActionTypesModal} from "../../redux/reducers/modalSearchReducer";
import {useTypedSelector} from "../../hooks/useTypedSelector";
import {CSSTransition} from "react-transition-group";
import ModalSearch from "../../components/ModalSearch/ModalSearch";
import SevenDaysHeader from "../../components/SevenDaysHeader/SevenDaysHeader";

const SevenDays = () => {
    const [isLoading, city, weatherDays] = useFetchSevenDays();
    const [selectedDay, setSelectedDay] = useState<number>(0);
    const day = weatherDays ? weatherDays[selectedDay] : undefined;
    const isOpenModal = useTypedSelector(state => state.modal.isOpen);
    const dispatch = useDispatch();
    const date = useNormalDate(day);
    if (isLoading) {
        return <div className='app__LoaderWrapper'><Loader marginTop={200}/></div>
    }
    return (
        <div className='sevenDays'>
            {isOpenModal ? <div className="background" onClick=
                {() => dispatch({type: ActionTypesModal.modalClose})}></div> : ''}
            <CSSTransition in={isOpenModal} unmountOnExit mountOnEnter
                           timeout={350} classNames='my-modal'>
                <ModalSearch/>
            </CSSTransition>
            <SevenDaysHeader city={city}/>
            <div className='sevenDays__dayInfo'>
                <div className='sevenDays__upperInfo'>
                    <div className='sevenDays__imgAndPrediction'>
                        <div className='sevenDays__currentState'>{day?.conditions}</div>
                        <div className='sevenDays__imgWrapper'><img src={day?.conditions === 'Clear' ? sun : cloud}
                                                                    alt=""/></div>
                    </div>
                    <div className='sevenDays__temp-date'>
                        <div className='sevenDays__tempInfo'>
                            <div className='sevenDays__highest tempEx'>Highest<br/>
                                <div className='sevenDays__tempEx'>{day?.tempmax}</div>
                            </div>
                            <div className='sevenDays__lowest tempEx'>Lowest<br/>
                                <div className='sevenDays__tempEx'>{day?.tempmin}</div>
                            </div>
                        </div>
                        <div className="sevenDays__date">{date}</div>
                    </div>
                </div>
                <div className="sevenDays__lowerInfo">
                    <IconsRow currentWeatherState={weatherDays[selectedDay]}/>
                </div>
            </div>
        </div>
    );
};

export default SevenDays;
