export enum ActionTypes{
    FETCH_WEATHER='FETCH_WEATHER',
}
interface IActions{
    type:ActionTypes
    payload:any
}
interface IWeatherDays{
    datetime:string,
    tempmax:number,
    tempmin:number,
    conditions:string
}
interface ICurrentSate{
    temp:string,
    windspeed:number,
    cloudcover:number,
    conditions:string,
    feelslike:number
}
interface  IWeather{
    resolvedAdress:string,
    days?:Array<IWeatherDays>
    currentState?: ICurrentSate
}
const defaultState:IWeather ={
    resolvedAdress:'Kyiv',
}
export  const weatherReducer = (state=defaultState,action:IActions)=>{
    switch (action.type){
        case ActionTypes.FETCH_WEATHER: return {...state,resolvedAdress:action.payload.resolvedAdress,
            days:action.payload.days, currentState:action.payload.currentConditions
        }
    }
}
