import React, {useEffect} from 'react';
import './App.scss';
import {useFetchWeather} from "./hooks/useFetchWeather";
import {useTypedSelector} from "./hooks/useTypedSelector";
import {useDispatch} from "react-redux";
import {ActionTypes} from "./redux/types/action-types";

const App: React.FC = () => {
    useFetchWeather();
    const dispatch = useDispatch();
    const currentWeatherCond = useTypedSelector(state => state.weather?.currentState?.conditions);
    const currentTheme = useTypedSelector(state => state.currentPage.bgColor);
    useEffect(()=>{
        if(currentWeatherCond===undefined){
            return
        }
        if(currentWeatherCond==='')
        dispatch({type:ActionTypes.SET_THEME,payload:currentWeatherCond})
    },[currentWeatherCond])
    return (
        <div className="App">
            <h1>Works</h1>
        </div>
    );
}

export default App;
