import {useDispatch} from "react-redux";
import {ActionTypesModal} from "../reducers/modalSearchReducer";

export const closeModal = ()=>{
    const dispatch = useDispatch();
    return dispatch({type:ActionTypesModal.modalClose})
}
export const openModal = ()=>{
    const dispatch = useDispatch();
    return dispatch({type:ActionTypesModal.modalOpen})
}
