import React from 'react';
import Loader from "../../components/Loader/Loader";
import {useFetchSevenDays} from "../../hooks/useFetchSevenDays";
import IconsRow from "../../components/IconsRow/IconsRow";
import {useNormalDate} from "../../hooks/useNormalDate";

const SevenDays = () => {
    const [isLoading, city,weatherDays] = useFetchSevenDays();
    const date = useNormalDate(weatherDays[0]);
    if (isLoading) {
        return <div className='app__LoaderWrapper'><Loader marginTop={200}/></div>
    }
    return (
        <div className='sevenDays'>
            <header className='sevenDays__wrapper'>
                <div className='sevenDays__header'>
                    <div className='sevenDays__goBackButton'><span className='sevenDays__goBack-circle'></span></div>
                    <div className='sevenDays__cityName'>{city}</div>
                    <div className='sevenDays__threeDots'><span className='sevenDays__dot'></span></div>
                </div>
                <div className='sevenDays__changeButton'></div>
            </header>
            <div className='sevenDays__dayInfo'>
                <div className='sevenDays__upperInfo'>
                    <div className='sevenDays__imgAndPrediction'>
                        <div className='sevenDays__currentState'></div>
                        <div className='sevenDays__imgWrapper'></div>
                    </div>
                    <div className='sevenDays__tempInfo'>
                        <div className='sevenDays__highest'></div>
                        <div className='sevenDays__lowest'></div>
                    </div>
                    <div className="sevenDays__date">{date}</div>
                </div>
                <div className="sevenDays__lowerInfo">
                    <IconsRow currentWeatherState={weatherDays[0]}/>
                </div>
            </div>
        </div>
    );
};

export default SevenDays;
