import React, {FC, useMemo} from 'react';
import './TodayForecast.scss';
import sun from '../../assets/sun.png';
import cloud from '../../assets/cloud.png';
import {useTypedSelector} from "../../hooks/useTypedSelector";
import {ICurrentSate} from "../../redux/reducers/weatherReducer";
const TodayForecast:FC = () => {
    const currentWeatherState:ICurrentSate = useTypedSelector(state=>state.weather?.currentState);
    const currentCity:string = useTypedSelector(state=>state.weather?.resolvedAdress);
    const daysLong = [{dayShort:'Mon',dayLong:'Monday'},{dayShort:'Tue',dayLong:'Tuesday'},
        {dayShort:'Wed',dayLong:'Wednesday'},{dayShort:'Thu',dayLong:'Thursday'},{dayShort:'Fri',dayLong:'Friday'},
        {dayShort:'Sat',dayLong:'Saturday'},{dayShort:'Sun',dayLong:'Sunday'}];
    function getLongDay(daySh:string){
        return daysLong.filter(day=> day.dayShort===daySh)[0].dayLong
    }
    const date = useMemo<string>(()=>{
         let fullDate:string = String(new Date(currentWeatherState?.datetimeEpoch*1000)).split('202')[0];
         const day:string = getLongDay(fullDate.split(' ')[0]);
         const newFullDate:string = fullDate.replace(fullDate.split(' ')[0],day);
         const dayNumber = newFullDate.split(' ')[1];
        return newFullDate.replace();
    },[currentWeatherState?.datetimeEpoch]);
    return (
        <div className='app__todayWrapper'>
            <div className="app__city">{currentCity}</div>
            <div className='app__changeBtn'>Change <span className='app__burger-menu'></span></div>
            <div className="app__imgWrapper">
                <img src={currentWeatherState?.conditions!=='Clear'?cloud:sun} alt=""/>
            </div>
            <div className='app__someInfo'>
                <div className='app__temp'>{currentWeatherState?.temp}</div>
                <div className='app__conditions'>{currentWeatherState?.conditions}</div>
                <div className='app__highestTemp'>{currentWeatherState?.conditions}</div>
            </div>
            <div className='app__someInfo'>{date}</div>
        </div>
    );
};

export default TodayForecast;
